mapper.update:
update checkresult
set Case_Number = #{caseNumber,jdbcType=VARCHAR},
  advice = #{advice,jdbcType=VARCHAR},
  result = #{result,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, Case_Number, advice, result
from checkresult
where id = #{id,jdbcType=INTEGER}
